import React, { Component } from "react";
import { DatePicker, Select, Button } from "antd";
import "antd/dist/antd.css";
import moment from "moment";

import UserService from "../services/user.service";
import ScheduleService from "../services/schedule.service";
import StationService from "../services/station.service";

const { RangePicker } = DatePicker;
// const { Option } = Select;

//moment().format("YYYY-MM-DD[T]HH:mm:ss"),
//  moment().startOf("month").utc(6).startOf("day"),
export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      content: "",
      schedule: [],
      stationCities: [],
      dateChoose: "",
      cityFrom: "",
      cityTo: "",
    };
    this.onOkDatePicker = this.onOkDatePicker.bind(this);
    this.onChangeCityFrom = this.onChangeCityFrom.bind(this);
    this.onChangeCityTo = this.onChangeCityTo.bind(this);
  }

  // onChange(value, dateString) {
  //   console.log("Selected Time: ", value);
  //   console.log("Formatted Selected Time: ", dateString);
  // }

  componentDidMount() {
    UserService.getPublicContent().then(
      (response) => {
        this.setState({
          content: response.data,
        });
      },
      (error) => {
        this.setState({
          content:
            (error.response && error.response.data) ||
            error.message ||
            error.toString(),
        });
      }
    );
    ScheduleService.getSchedule().then(
      (response) => {
        this.setState({
          schedule: response.data,
        });
      },
      (error) => {
        this.setState({
          content:
            (error.response && error.response.data) ||
            error.message ||
            error.toString(),
        });
      }
    );
    StationService.getStationCities().then(
      (response) => {
        this.setState({
          stationCities: response.data,
        });
      },
      (error) => {
        this.setState({
          content:
            (error.response && error.response.data) ||
            error.message ||
            error.toString(),
        });
      }
    );
  }

  onOkDatePicker(value, dateString) {
    this.setState({
      dateChoose: moment(value.toISOString())
        .utc(6)
        .format("YYYY-MM-DD[T]HH:mm:ss"),
    });
  }
  onChangeCityFrom(value) {
    this.setState({
      cityFrom: value,
    });
  }
  onChangeCityTo(value) {
    this.setState({
      cityTo: value,
    });
  }

  render() {
    console.log(this.state.dateChoose);
    console.log(this.state.cityFrom);
    console.log(this.state.cityTo);
    return (
      <div className="container">
        <header className="jumbotron">
          <h3>{this.state.content}</h3>
          <Select
            showSearch
            style={{ width: 200 }}
            placeholder="Select a person"
            optionFilterProp="children"
            onChange={this.state.onChangeCityFrom()}
            filterOption={(input, option) =>
              option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
            }
          >
            {this.state.stationCities.map((e, index) => (
              <Select.Option key={index} value={e}>
                {e}
              </Select.Option>
            ))}
          </Select>
          <Select
            showSearch
            style={{ width: 200 }}
            placeholder="Select a person"
            optionFilterProp="children"
            onChange={this.state.onChangeCityTo()}
            filterOption={(input, option) =>
              option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
            }
          >
            {this.state.stationCities.map((e, index) => (
              <Select.Option key={index} value={e}>
                {e}
              </Select.Option>
            ))}
          </Select>

          <DatePicker
            showTime
            // onChange={this.onChange}
            format="YYYY-MM-DDTHH:mm"
            onOk={this.onOkDatePicker}
            renderExtraFooter={() => <h5>Please press OK after choosing</h5>}
          />
          <Button type="primary" onClick={() => this.enterLoading(0)}>
            Click me!
          </Button>
        </header>
      </div>
    );
  }
}
